Scripts.OnVerticalScroll:
local scrollbar = getglobal(this:GetName().."ScrollBar");
local val = scrollbar:GetValue()
local min, max = scrollbar:GetMinMaxValues();

if arg1 == min then
	getglobal(scrollbar:GetName().."ScrollUpButton"):Disable();
else
	getglobal(scrollbar:GetName().."ScrollUpButton"):Enable();
end
if scrollbar:GetValue() == max then
	getglobal(scrollbar:GetName().."ScrollDownButton"):Disable();
else
	getglobal(scrollbar:GetName().."ScrollDownButton"):Enable();
end

Outfitter:OnScroll(this)

Scripts.OnEnter:
if this:GetChecked() then
	GameTooltip_AddNewbieTip(Outfitter.cAutoSwitch, 1.0, 1.0, 1.0, Outfitter.cAutoSwitchOnDescription, 1);
else
	GameTooltip_AddNewbieTip(Outfitter.cAutoSwitch, 1.0, 1.0, 1.0, Outfitter.cAutoSwitchOffDescription, 1);
end

if this:GetChecked() then
	GameTooltip_AddNewbieTip(Outfitter.cTooltipInfo, 1.0, 1.0, 1.0, Outfitter.cTooltipInfoOnDescription, 1);
else
	GameTooltip_AddNewbieTip(Outfitter.cTooltipInfo, 1.0, 1.0, 1.0, Outfitter.cTooltipInfoOffDescription, 1);
end

Scripts.OnHide:
if not PlayerTitleDropDown.titleCount or PlayerTitleDropDown.titleCount >= 1 then
	PlayerTitleDropDown:Show();
end

Scripts.OnMouseUp:
Outfitter.MinimapDropDown_OnLoad();
this.ChangedValueFunc = Outfitter.MinimapButton_ItemSelected;
ToggleDropDownMenu(nil, nil, this, this, 22, 1);

-- Hack to force the menu to position correctly.  UIDropDownMenu code
-- keeps thinking that it's off the screen and trying to reposition
-- it, which it does very poorly

Outfitter.MinimapDropDown_AdjustScreenPosition(this);

PlaySound("igMainMenuOptionCheckBoxOn");
